mapper.select:
select a.pz_apply_id,a.pz_apply_grade,c.case_id,c.case_No,c.case_name,c.incident_place, to_char(c.accept_date,'yyyy-mm-dd hh24:mi:ss') accept_date, to_char(c.incident_date,'yyyy-mm-dd hh24:mi:ss') incident_Date, c.JQ_NO,c.CASE_AJLY,c.lat_X,c.lon_Y,c.case_type_imp, c.accept_Unit,c.case_Type,st.value_desc case_Type_value,c.case_Desc,c.case_status from pz_t_apply a, case_info c ,(select * from sy_t_codevalue s where s.type_id='2005') st where a.case_id(+) = c.case_id and c.case_type = st.code_value(+)
and a.pz_apply_id = #{pzid}
and c.case_no = #{caseNo}

mapper.update:
update pz_t_apply
t
set t.pz_apply_grade = #{pzApplyGrade}
where t.pz_apply_id = #{pzid}

mapper.delete:
delete from
pz_t_clue_info t
where t.pz_id=#{pzid}

mapper.select:
select t.clue_id
from pz_t_clue_info
t
where 
 t.pz_id = #{pzid}
 and t.CLUE_AUXILIARY = #{userId}

  	select count(1) from sop_lajds l
	 where l.ajbh=#{ajbh}
	   and l.isdel='0'

  	select count(1) from sop_sadjb s
	 where s.ajbh=#{ajbh}
	   and s.isdel='0'
